String.prototype.sub=function(){if(arguments.length==0)return this;var e=arguments.length>1?Array.prototype.slice.call(arguments):arguments[0];if(Object.prototype.toString.call(e)==="[object Array]"){return this.replace(/%(\d+)/g,function(t,n){return typeof e[n-1]!="undefined"?e[n-1]:t})}else if(Object.prototype.toString.call(e)==="[object Object]"){return this.replace(/{(\S+?)}/g,function(t,n){return typeof e[n]!="undefined"?e[n]:t})}return this.toString()}